import React from "react";

const Card = ({ data, state }) => {
  return (
    <div className={`cardContainer ${state}`}>
      {data.map((curItem, index) =>
        curItem.urlToImage ? (
          <div className={`news-card ${state}`} key={index}>
            <img src={curItem.urlToImage} alt="news" className="news-image" />
            <div className="content">
              <a
                className="title"
                href={curItem.url}
                target="_blank"
                rel="noopener noreferrer"
              >
                {curItem.title}
              </a>
              <p>{curItem.description}</p>
              <button
                className="read-more-btn"
                onClick={() => window.open(curItem.url)}
              >
                Read More
              </button>
            </div>
          </div>
        ) : null
      )}
    </div>
  );
};

export default Card;
