import React, { useEffect, useState } from "react";
import Card from "./Card";
import "@fortawesome/fontawesome-free/css/all.min.css";
import jsPDF from "jspdf";

/**
 * Main News Application Component
 * Handles news display, notes functionality, and user preferences
 */
const Newsapp = () => {
  // ============= STATE MANAGEMENT =============
  const [showHero, setShowHero] = useState(true);
  const [state, setState] = useState("light-mode"); // Theme state
  const [showNotes, setShowNotes] = useState(false); // Notes panel visibility
  const [isHeroSliding, setIsHeroSliding] = useState(false); // Hero section animation
  const [notes, setNotes] = useState([]); // User's notes
  const [newNote, setNewNote] = useState({ title: "", content: "" }); // New note draft
  const [search, setSearch] = useState(""); // News search term
  const [newsData, setNewsData] = useState(null); // Fetched news data
  const [showFilters, setShowFilters] = useState(false); // Filter panel visibility
  const [dateFilter, setDateFilter] = useState("newest"); // Date sorting filter
  const [selectedSources, setSelectedSources] = useState([]); // Selected news sources
  const [availableSources, setAvailableSources] = useState(new Set()); // Available sources from API
  const API_KEY = "67be3fd527784b2eb50f08b02c1e5352";

  // ============= SCROLL HANDLING =============
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 10 && !isHeroSliding) {
        setIsHeroSliding(true);
        setTimeout(() => setShowHero(false), 500);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [isHeroSliding]);

  // ============= THEME MANAGEMENT =============
  const toggleMode = () => {
    setState((prev) => (prev === "light-mode" ? "dark-mode" : "light-mode"));
  };

  useEffect(() => {
    document.body.className = state;
  }, [state]);

  // ============= NEWS DATA FETCHING =============
  const getData = async () => {
    try {
      const searchTerm = search.trim() || "india";
      const response = await fetch(
        `https://newsapi.org/v2/everything?q=${searchTerm}&apiKey=${API_KEY}`
      );
      const jsonData = await response.json();

      // Process sources
      const sources = new Set(
        jsonData.articles.map((article) => article.source.name)
      );
      setAvailableSources(sources);

      // Apply filters
      let filtered = jsonData.articles
        .sort((a, b) =>
          dateFilter === "newest"
            ? new Date(b.publishedAt) - new Date(a.publishedAt)
            : new Date(a.publishedAt) - new Date(b.publishedAt)
        )
        .filter(
          (article) =>
            selectedSources.length === 0 ||
            selectedSources.includes(article.source.name)
        );

      setNewsData(filtered.slice(0, 10));
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  useEffect(() => {
    getData();
  }, [search, dateFilter, selectedSources]);

  // ============= SOURCE FILTER HANDLING =============
  const handleSourceChange = (source) => {
    setSelectedSources((prev) =>
      prev.includes(source)
        ? prev.filter((s) => s !== source)
        : [...prev, source]
    );
  };

  // ============= NOTES FUNCTIONALITY =============
  // Load/save notes from/to localStorage
  useEffect(() => {
    const savedNotes = localStorage.getItem("userNotes");
    if (savedNotes) setNotes(JSON.parse(savedNotes));
  }, []);

  useEffect(() => {
    localStorage.setItem("userNotes", JSON.stringify(notes));
  }, [notes]);

  const handleAddNote = () => {
    if (newNote.title.trim() && newNote.content.trim()) {
      setNotes([
        ...notes,
        {
          ...newNote,
          id: Date.now(),
          date: new Date(),
        },
      ]);
      setNewNote({ title: "", content: "" });
    }
  };

  const handleDeleteNote = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
  };

  // ============= PDF EXPORT FUNCTIONALITY =============
  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPos = 20;

    doc.setFontSize(22);
    doc.text("My Notes", 20, 15);
    doc.setFontSize(12);

    notes.forEach((note, index) => {
      doc.setFont("helvetica", "bold");
      doc.text(`Note ${index + 1}: ${note.title}`, 20, yPos);
      doc.setFont("helvetica", "normal");

      const contentLines = doc.splitTextToSize(note.content, 170);
      contentLines.forEach((line) => {
        if (yPos > 280) {
          doc.addPage();
          yPos = 20;
        }
        doc.text(line, 20, yPos + 10);
        yPos += 10;
      });

      yPos += 20;
      if (index < notes.length - 1) doc.line(20, yPos, 190, yPos);
      yPos += 10;
    });

    doc.save(`notes-${new Date().toISOString().split("T")[0]}.pdf`);
  };

  // ============= COMPONENT RENDERING =============
  return (
    <div className={state}>
      {/* NAVIGATION BAR */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        {/* ... (keep existing navbar structure) ... */}
      </nav>

      {/* FILTER PANEL */}
      <div className={`filter-panel ${showFilters ? "open" : ""}`}>
        {/* ... (keep existing filter panel structure) ... */}
      </div>

      {/* MAIN CONTENT AREA */}
      {showNotes ? (
        // NOTES INTERFACE
        <div className="notes-container container mt-4">
          {/* ... (keep existing notes interface) ... */}
        </div>
      ) : showHero ? (
        // HERO SECTION
        <div
          className={`hero-section ${state} ${isHeroSliding ? "slide-up" : ""}`}
        >
          {/* ... (keep existing hero section) ... */}
        </div>
      ) : (
        // NEWS DISPLAY
        <div className={`news-container ${showFilters ? "filter-open" : ""}`}>
          {newsData ? (
            <Card data={newsData} state={state} showFilters={showFilters} />
          ) : (
            <p className="loading-text">Loading news...</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Newsapp;
