import React, { useEffect, useState } from "react";
import Card from "./Card";
import "@fortawesome/fontawesome-free/css/all.min.css";
import jsPDF from "jspdf";

const Newsapp = () => {
  const [showHero, setShowHero] = useState(true);
  const [state, setState] = useState("light-mode");
  const [showNotes, setShowNotes] = useState(false);
  const [isHeroSliding, setIsHeroSliding] = useState(false);
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState({ title: "", content: "" });
  const [search, setSearch] = useState("");
  const [newsData, setNewsData] = useState(null);
  const API_KEY = "67be3fd527784b2eb50f08b02c1e5352";

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 10 && !isHeroSliding) {
        setIsHeroSliding(true);
        setTimeout(() => setShowHero(false), 500); // Match animation duration
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [isHeroSliding]);
  // Theme handling
  const toggleMode = () => {
    setState((prev) => (prev === "light-mode" ? "dark-mode" : "light-mode"));
  };

  useEffect(() => {
    document.body.className = state;
  }, [state]);

  // News data fetching
  const getData = async () => {
    try {
      const searchTerm = search.trim() || "india";
      const response = await fetch(
        `https://newsapi.org/v2/everything?q=${searchTerm}&apiKey=${API_KEY}`
      );
      const jsonData = await response.json();
      setNewsData(jsonData.articles.slice(0, 10));
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  useEffect(() => {
    getData();
  }, [search]);

  // Notes functionality
  useEffect(() => {
    const savedNotes = localStorage.getItem("userNotes");
    if (savedNotes) setNotes(JSON.parse(savedNotes));
  }, []);

  useEffect(() => {
    localStorage.setItem("userNotes", JSON.stringify(notes));
  }, [notes]);

  const handleAddNote = () => {
    if (newNote.title.trim() && newNote.content.trim()) {
      setNotes([...notes, { ...newNote, id: Date.now(), date: new Date() }]);
      setNewNote({ title: "", content: "" });
    }
  };

  const handleDeleteNote = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPos = 20;

    doc.setFontSize(22);
    doc.text("My Notes", 20, 15);
    doc.setFontSize(12);

    notes.forEach((note, index) => {
      doc.setFont("helvetica", "bold");
      doc.text(`Note ${index + 1}: ${note.title}`, 20, yPos);
      doc.setFont("helvetica", "normal");

      const contentLines = doc.splitTextToSize(note.content, 170);
      contentLines.forEach((line) => {
        if (yPos > 280) {
          doc.addPage();
          yPos = 20;
        }
        doc.text(line, 20, yPos + 10);
        yPos += 10;
      });

      yPos += 20;
      if (index < notes.length - 1) doc.line(20, yPos, 190, yPos);
      yPos += 10;
    });

    doc.save(`notes-${new Date().toISOString().split("T")[0]}.pdf`);
  };

  return (
    <div className={state}>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <a
            className="navbar-brand text-white"
            href="#"
            onClick={() => window.location.reload()}
          >
            informed
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a
                  className="nav-link text-white"
                  href="#"
                  onClick={(e) => {
                    e.preventDefault();
                    setShowNotes(true);
                  }}
                >
                  Notes
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link text-white" href="#">
                  Trending
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle text-white"
                  href="#"
                  role="button"
                  data-bs-toggle="dropdown"
                >
                  Categories
                </a>
                <ul className="dropdown-menu bg-dark">
                  {["sports", "politics", "entertainment", "health"].map(
                    (category) => (
                      <li key={category}>
                        <button
                          className="dropdown-item text-white"
                          onClick={() => {
                            setSearch(category);
                            setShowNotes(false);
                            getData();
                          }}
                        >
                          {category.charAt(0).toUpperCase() + category.slice(1)}
                        </button>
                      </li>
                    )
                  )}
                </ul>
              </li>
            </ul>

            {/* Search and Theme Toggle */}
            <div className="d-flex align-items-center">
              <form
                className="d-flex me-3"
                onSubmit={(e) => {
                  e.preventDefault();
                  getData();
                }}
              >
                <input
                  className="form-control me-3 bg-dark text-white border-light"
                  type="search"
                  placeholder="Search News"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
                <button className="btn btn-outline-light" type="submit">
                  Search
                </button>
              </form>
              <div className="icon" onClick={toggleMode}>
                {state === "light-mode" ? (
                  <i className="fa-solid fa-moon fs-4 text-white"></i>
                ) : (
                  <i className="fa-solid fa-sun fs-4 text-white"></i>
                )}
              </div>
            </div>
          </div>
        </div>
      </nav>
      {/* Main Content */}
      {showNotes ? (
        <div className="notes-container container mt-4">
          <div className="row justify-content-center">
            <div className="col-md-8">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h3>My Notes</h3>
                <button
                  className="btn btn-success"
                  onClick={exportToPDF}
                  disabled={notes.length === 0}
                >
                  <i className="fas fa-file-pdf me-2"></i>Export All as PDF
                </button>
              </div>

              <div className="mb-4">
                <input
                  type="text"
                  className={`form-control mb-2 ${
                    state === "dark-mode" ? "bg-dark text-white" : ""
                  }`}
                  placeholder="Note Title"
                  value={newNote.title}
                  onChange={(e) =>
                    setNewNote({ ...newNote, title: e.target.value })
                  }
                />
                <textarea
                  className={`form-control ${
                    state === "dark-mode" ? "bg-dark text-white" : ""
                  }`}
                  placeholder="Write your note here..."
                  rows="5"
                  value={newNote.content}
                  onChange={(e) =>
                    setNewNote({ ...newNote, content: e.target.value })
                  }
                ></textarea>
                <button
                  className={`btn ${
                    state === "dark-mode" ? "dark-mode" : "light-mode"
                  } add-note-btn`}
                  onClick={handleAddNote}
                >
                  Add Note
                </button>
              </div>

              {notes.map((note) => (
                <div
                  key={note.id}
                  className={`card mb-3 ${
                    state === "dark-mode" ? "bg-dark text-white" : ""
                  }`}
                >
                  <div className="card-body">
                    <div className="d-flex justify-content-between align-items-center">
                      <h5 className="card-title">{note.title}</h5>
                      <small className="text-muted">
                        {new Date(note.date).toLocaleString()}
                      </small>
                    </div>
                    <pre
                      className="card-text"
                      style={{ whiteSpace: "pre-wrap" }}
                    >
                      {note.content}
                    </pre>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDeleteNote(note.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      ) : showHero ? (
        <div
          className={`hero-section ${state} d-flex flex-column justify-content-center align-items-center ${
            isHeroSliding ? "slide-up" : ""
          }`}
          style={{ minHeight: "100vh" }}
        >
          {/* Centered Content Container */}
          <div
            className="text-center"
            style={{
              maxWidth: "80%",
              margin: "0 auto",
              transform: "translateY(-10%)",
            }}
          >
            <h1 className="display-1 fade-in mb-2">informed</h1>
            <h2
              className="h3 fade-in"
              style={{ color: state === "light-mode" ? "#1976d2" : "#0d47a1" }}
            >
              SMARTER NEWS. BETTER DECISIONS
            </h2>
          </div>

          <div className="scroll-indicator mt-5">
            <i className="fas fa-chevron-down fa-2x bounce"></i>
          </div>
        </div>
      ) : (
        <div className="news-container">
          {newsData ? (
            <Card data={newsData} state={state} />
          ) : (
            <p className="loading-text">Loading news...</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Newsapp;
