import React, { useEffect, useState } from "react";
import Card from "./Card";
import "@fortawesome/fontawesome-free/css/all.min.css";
import jsPDF from "jspdf";

const Newsapp = () => {
  const [state, setState] = useState("light-mode");
  const [showNotes, setShowNotes] = useState(false);
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState({ title: "", content: "" });
  const [search, setSearch] = useState("");
  const [newsData, setNewsData] = useState(null);
  const [filters, setFilters] = useState({
    sortBy: "publishedAt",
    sources: [],
  });
  const [availableSources, setAvailableSources] = useState([]);
  const API_KEY = "c4f2cc73493c497c9d23ae6608a7f313";

  // Theme handling
  const toggleMode = () => {
    setState((prev) => (prev === "light-mode" ? "dark-mode" : "light-mode"));
  };

  useEffect(() => {
    document.body.className = state;
  }, [state]);

  // News data fetching
  const getData = async () => {
    try {
      const searchTerm = search.trim() || "india";
      const params = new URLSearchParams({
        q: searchTerm,
        sortBy: filters.sortBy,
        apiKey: API_KEY,
      });

      const response = await fetch(
        `https://newsapi.org/v2/everything?${params}`
      );
      const jsonData = await response.json();

      // Extract unique sources
      const sources = [
        ...new Set(jsonData.articles.map((article) => article.source.name)),
      ];
      setAvailableSources(sources);

      // Filter articles
      let filteredArticles = jsonData.articles;
      if (filters.sources.length > 0) {
        filteredArticles = filteredArticles.filter((article) =>
          filters.sources.includes(article.source.name)
        );
      }

      setNewsData(filteredArticles.slice(0, 10));
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      if (
        search.trim().length > 0 ||
        filters.sortBy ||
        filters.sources.length > 0
      ) {
        getData();
      }
    }, 500);

    return () => clearTimeout(debounceTimer);
  }, [search, filters.sortBy, filters.sources]);

  // Notes functionality
  useEffect(() => {
    const savedNotes = localStorage.getItem("userNotes");
    if (savedNotes) setNotes(JSON.parse(savedNotes));
  }, []);

  useEffect(() => {
    localStorage.setItem("userNotes", JSON.stringify(notes));
  }, [notes]);

  const handleAddNote = () => {
    if (newNote.title.trim() && newNote.content.trim()) {
      setNotes([...notes, { ...newNote, id: Date.now(), date: new Date() }]);
      setNewNote({ title: "", content: "" });
    }
  };

  const handleDeleteNote = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPos = 20;

    doc.setFontSize(22);
    doc.text("My Notes", 20, 15);
    doc.setFontSize(12);

    notes.forEach((note, index) => {
      doc.setFont("helvetica", "bold");
      doc.text(`Note ${index + 1}: ${note.title}`, 20, yPos);
      doc.setFont("helvetica", "normal");

      const contentLines = doc.splitTextToSize(note.content, 170);
      contentLines.forEach((line) => {
        if (yPos > 280) {
          doc.addPage();
          yPos = 20;
        }
        doc.text(line, 20, yPos + 10);
        yPos += 10;
      });

      yPos += 20;
      if (index < notes.length - 1) doc.line(20, yPos, 190, yPos);
      yPos += 10;
    });

    doc.save(`notes-${new Date().toISOString().split("T")[0]}.pdf`);
  };

  return (
    <div className={state}>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <a
            className="navbar-brand text-white"
            href="#"
            onClick={() => window.location.reload()}
          >
            NewsBook
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a
                  className="nav-link text-white"
                  href="#"
                  onClick={(e) => {
                    e.preventDefault();
                    setShowNotes(true);
                  }}
                >
                  Notes
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle text-white"
                  href="#"
                  role="button"
                  data-bs-toggle="dropdown"
                >
                  <i className="fas fa-filter me-1"></i> Filters
                </a>
                <ul
                  className={`dropdown-menu ${
                    state === "dark-mode" ? "bg-dark" : "bg-light"
                  }`}
                >
                  {/* Categories Section */}
                  <li>
                    <div className="dropdown-header">Categories</div>
                    <div
                      className="d-flex flex-wrap px-2"
                      style={{ gap: "0.5rem" }}
                    >
                      {["sports", "politics", "entertainment", "health"].map(
                        (category) => (
                          <button
                            key={category}
                            className={`btn btn-sm ${
                              state === "dark-mode"
                                ? "btn-outline-light"
                                : "btn-outline-dark"
                            }`}
                            onClick={() => {
                              setSearch(category);
                              setShowNotes(false);
                            }}
                          >
                            {category.charAt(0).toUpperCase() +
                              category.slice(1)}
                          </button>
                        )
                      )}
                    </div>
                    <hr className="dropdown-divider" />
                  </li>

                  {/* Sort Section */}
                  <li>
                    <div className="dropdown-item">
                      <label className="small mb-1">Sort By:</label>
                      <select
                        className={`form-control ${
                          state === "dark-mode" ? "bg-secondary text-white" : ""
                        }`}
                        value={filters.sortBy}
                        onChange={(e) =>
                          setFilters((prev) => ({
                            ...prev,
                            sortBy: e.target.value,
                          }))
                        }
                      >
                        <option value="publishedAt">Newest First</option>
                        <option value="relevancy">Most Relevant</option>
                      </select>
                    </div>
                    <hr className="dropdown-divider" />
                  </li>

                  {/* Sources Section */}
                  <li>
                    <div className="dropdown-item">
                      <label className="small mb-1">News Sources:</label>
                      <div
                        className="source-filters"
                        style={{ maxHeight: "200px", overflowY: "auto" }}
                      >
                        {availableSources.map((source) => (
                          <div key={source} className="form-check">
                            <input
                              type="checkbox"
                              className="form-check-input"
                              id={source}
                              checked={filters.sources.includes(source)}
                              onChange={(e) => {
                                const newSources = e.target.checked
                                  ? [...filters.sources, source]
                                  : filters.sources.filter((s) => s !== source);
                                setFilters((prev) => ({
                                  ...prev,
                                  sources: newSources,
                                }));
                              }}
                            />
                            <label
                              className="form-check-label"
                              htmlFor={source}
                            >
                              {source}
                            </label>
                          </div>
                        ))}
                      </div>
                    </div>
                  </li>
                </ul>
              </li>
            </ul>

            {/* Search and Theme Toggle */}
            <div className="d-flex align-items-center">
              <div className="me-3" style={{ width: "300px" }}>
                <input
                  className={`form-control ${
                    state === "dark-mode" ? "bg-dark text-white" : ""
                  }`}
                  type="search"
                  placeholder="Search News..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                />
              </div>
              <div className="icon" onClick={toggleMode}>
                {state === "light-mode" ? (
                  <i className="fa-solid fa-moon fs-4 text-white"></i>
                ) : (
                  <i className="fa-solid fa-sun fs-4 text-white"></i>
                )}
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content remains same as previous version */}
      {/* ... rest of the code remains unchanged ... */}
    </div>
  );
};

export default Newsapp;
