import React from "react";

const Card = ({ data, state }) => {
  const readMore = (url) => {
    window.open(url);
  };

  const downloadPDF = (title, content) => {
    const doc = new jsPDF();
    doc.setFont("helvetica", "bold");
    doc.text(title, 10, 10); // Title at the top

    doc.setFont("helvetica", "normal");
    const splitText = doc.splitTextToSize(
      content || "No description available.",
      180
    );
    doc.text(splitText, 10, 20); // Wrap text properly

    const filename = `${title.replace(/[^a-zA-Z0-9]/g, "_")}.pdf`;
    doc.save(filename);
  };

  return (
    <div className={`cardContainer ${state}`}>
      {data.map((curItem, index) => {
        if (!curItem.urlToImage) return null;

        return (
          <div className={`card ${state}`} key={index}>
            <img src={curItem.urlToImage} alt="news" />
            <div className={`content ${state}`}>
              <a
                className={`title ${state}`}
                onClick={() => window.open(curItem.url)}
              >
                {curItem.title}
              </a>
              <p className={state}>{curItem.description}</p>
              <button
                className={`read-more ${state}`}
                onClick={() => window.open(curItem.url)}
              >
                Read More
              </button>
              <button
                className={`download ${state}`}
                onClick={() => downloadPDF(curItem.title, curItem.description)}
              >
                Download PDF
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Card;
