import { AlertCircle } from "lucide-react";

const WarningBanner = () => (
  <div className="flex items-center bg-yellow-100 text-yellow-800 p-2 rounded-md mb-2">
    <AlertCircle size={18} className="mr-2" />
    <span>Some articles may not be downloadable due to website restrictions.</span>
  </div>
);

export default WarningBanner;
